import React, { PureComponent } from "react";
import styled from "styled-components";
import Pagination from "rc-pagination";
import "rc-pagination/assets/index.css";
import Movie from "./Movie.1";

class MovieList extends PureComponent {
  constructor() {
    super();
    this.state = {
      movies: [],
      current: 1
    };
  }
  onChange = page => {
    console.log(page);
    this.setState(
      {
        current: page,
        loading: true
      }
    );
    this.initialloading();
  };

  async initialloading() {
    try {
      const res = await fetch(
        `http://www.omdbapi.com/?i=tt3896198&apikey=fa281222&s=Batman&page=${
          this.state.current
        }`
      );
      const movies = await res.json();
      console.log(movies);
      this.setState({
        // loading: false,
        movies: movies.Search,
        totalpages: movies.totalResults
      });
    } catch (e) {
    //   this.setState({
    //     loading: false
    //   });
    //   console.log(e);
    }
  }

  componentDidMount() {
    this.initialloading();
  }

  render() {
    const { movies, totalpages, current } = this.state;
    console.log(this.props);
    return (
      <div>
        <MovieGrid>
          {movies.map((movie, id) => (
            <Movie key={id} details={movie} />
          ))}
        </MovieGrid>
        {totalpages && totalpages > 10 ? (
          <Paginationcont>
            <Pagination
              onChange={this.onChange}
              current={current}
              total={totalpages}
            />
          </Paginationcont>
        ) : (
          ""
        )}
      </div>
    );
  }
}

export default MovieList;

const MovieGrid = styled.div`
  display: grid;
  padding: 1rem;
  grid-template-columns: repeat(5, 1fr);
  grid-row-gap: 1rem;
  text-align: center;
`;

const Paginationcont = styled.div`
  display: flex;
  justify-content: center;
`;
