import React, { Component } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import styled from "styled-components";
import Overdrive from "react-overdrive";

class Movie extends Component {
  state = {
    imageLoadError: true
  };
  render() {
    const { details } = this.props;
    return (
      <div>
        <Link to={`/${details.imdbID}`}>
          {details.Poster && details.Poster !== "N/A" && imageLoadError ? (
            <Overdrive id={details.imdbID}>
              <Poster
                src={details.Poster}
                alt={details.Title}
                onError={e => {
                  if (imageLoadError) {
                    this.setState({
                      imageLoadError: false
                    });
                    // e.target.src = 'fallbackImage.png';
                  }
                }}
              />
            </Overdrive>
          ) : (
            <Poster
              src={require("./image-not-available.jpg")}
              alt={details.Title}
            />
          )}
        </Link>
        <div>Name: {details.Title}</div>
        <div>Year: {details.Year}</div>
        <div>imdbID: {details.imdbID}</div>
        <div>Type: {details.Type}</div>
      </div>
    );
  }
}

export default Movie;

Movie.propTypes = {
  details: PropTypes.shape({
    Poster: PropTypes.string.isRequired,
    Title: PropTypes.string.isRequired,
    Year: PropTypes.string.isRequired,
    imdbID: PropTypes.string.isRequired,
    Type: PropTypes.string.isRequired
  }).isRequired
};

export const Poster = styled.img`
  margin-top: 20px;
  width: 200px;
  height: 275px;
  box-shadow: 0 0 10px #000;
`;
