export const MOVIE_SEARCH = "MOVIE_SEARCH";

const initialState = {
  loading: false
};

function movieSearch(state = initialState, action) {
  switch (action.type) {
    case MOVIE_SEARCH:
      return [...state, action.payload];
    default:
      return state;
  }
}

export default movieSearch;
