import { MOVIE_SEARCH } from './reducers';

// const urlapi = `https://reqres.in/api`;

export function searchMovie(res) {
  return {
    type: MOVIE_SEARCH,
    payload: res
  };
}

export function searchMovies(searchTerm, page) {
  return (dispatch) => {
    const url = `http://www.omdbapi.com/?i=tt3896198&apikey=fa281222&s=${searchTerm}&page=${
      page
    }`;

    fetch(url)
      .then(res => console.log(res))
      .catch(err => console.log(err))
      .then((res) => {
        console.log(res);
        dispatch(searchMovie(res));
      });
  };
}
