import React, { Component } from 'react';
import styled from 'styled-components';
import Overdrive from'react-overdrive';
import { Poster } from './Movie';
import backdrop from './backdrop.svg'

// import Poster from './Movie';

class MovieDetail extends Component {

    state = {
        movie: {}
    }

    async componentDidMount() {
        try {
            const res = await fetch(`http://www.omdbapi.com/?i=${this.props.match.params.id}&apikey=fa281222`);
            const movie = await res.json();
            this.setState({
                movie: movie
            });
            console.log(movie)
        } catch (e) {
            console.log(e);
        }
    }

    render(){
        console.log(this.props)
        console.log(this.state)
        const { movie } = this.state;
        return (
            <MovieContainer>
                <Movieinfo>
                <Overdrive id={movie.imdbID}>
                    <Poster src={movie.Poster} alt={movie.Title}/>
                    </Overdrive>
                    <div>
                        <h1>{movie.Title}</h1>
                        <h2>{movie.Year}</h2>
                        <h2>{movie.Type}</h2>
                        <div>imdbID: {movie.imdbID}</div>                        
                    </div>
                </Movieinfo>
            </MovieContainer>
        );
    }
}

export default MovieDetail;

const MovieContainer = styled.div`
    position: relative;
    padding-top: calc(50vh - 60px);
    background: url(${backdrop}) no-repeat;
    backdrop-size: cover;
`;

const Movieinfo = styled.div`
    background: #fff;
    padding: 1rem 10%;
    display: flex;
    color: #282d35;
    > div {
        margin-left: 3rem;
        > h1 {
            position: relative;
            top: -11rem;
            // left: 15rem;
            text-shadow: 0 0 2px #737272;
        }
        > h2 {
            margin: 0px
        }
    }

    img {
        position: relative;
        top: -15rem;
    }
`;
