//         `http://www.omdbapi.com/?i=tt3896198&apikey=fa281222&s=Batman&page=${
//           this.state.current
//         }`
//       );
//       const movies = await res.json();
//       this.setState({
//         movies: movies.Search,
//         totalpages: parseInt(movies.totalResults)
//       });
//     } catch (e) {
//       console.log(e);
//     }
//   }

  render() {
    const { current, totalpages } = this.state;
    const { movies } = this.props;
    console.log(this.props)
    return (
      <div>
        <MovieContainer>
          {movies.map((movie, id) => (
            <Movie key={id} details={movie} />
          ))}
        </MovieContainer>
        <Row
          className="justify-content-md-center"
          style={{ justifyContent: "center" }}
        >
          {totalpages && totalpages > 10 ? (
            <Pagination
              onChange={this.onChange}
              current={current}
              total={totalpages}
            />
          ) : (
            ""
          )}
        </Row>
      </div>
    );
  }
}

function mapStateToProps(state) {
  console.log(state);
  return {movies: state.movies}
}

export default connect(
  mapStateToProps,
  { searchMovies }
)(MovieList);

const MovieContainer = styled(Row)`
  padding: 0 3rem;
  margin: 0 auto;
  justify-content: center;
`;
