import React, { Component } from 'react';
import styled from 'styled-components';
import Movie,{ Poster } from './Movie';
import backdrop from './backdrop.svg'

// import Poster from './Movie';

class MovieDetail extends Component {

    state = {
        movie: {}
    }

    async componentDidMount() {
        try {
            const res = await fetch(`http://www.omdbapi.com/?i=${this.props.match.params.id}&apikey=fa281222`);
            const movie = await res.json();
            this.setState({
                movie: movie
            });
            console.log(movie)
        } catch (e) {
            console.log(e);
        }
    }

    render(){
        console.log(this.props)
        console.log(this.state)
        const { movie } = this.state;
        return (
            <MovieContainer>
                <Movieinfo>
                    <Poster src={movie.Poster} alt={movie.Title}/>
                    <h1>{movie.Title}</h1>
                    <div>
                        <div>Year: {movie.Year}</div>
                        <div>imdbID: {movie.imdbID}</div>
                        <div>Type: {movie.Type}</div>
                    </div>
                </Movieinfo>
            </MovieContainer>
        );
    }
}

export default MovieDetail;

const MovieContainer = styled.div`
    position: relative;
    padding-top: 50vh;
    background: url(${backdrop}) no-repeat;
    backdrop-size: cover;
`;

const Movieinfo = styled.div`
    background: #fff;
    padding: 1rem 10%;
    display: flex;
    h1 {
        position: relative;
        top: -10rem;
        left: 5rem;
        color: #1b3d86
    }
    > div {
        margin-left: 20px;
    }
    img {
        position: relative;
        top: -15rem;
    }
`;
