import React, { Component } from 'react';
import styled from 'styled-components';
import Pagination from 'rc-pagination';
import 'rc-pagination/assets/index.css';
import Movie from './Movie';

class MovieList extends Component {

    state = {
        movies: [],
        current: 1,
    }

    onChange = (page) => {
        console.log(page);
        this.setState({
          current: page,
        });
        this.initialloading();
      }

    async initialloading() {
        try {
            const res = await fetch(`http://www.omdbapi.com/?i=tt3896198&apikey=fa281222&s=Batman&page=${this.state.current}`);
            const movies = await res.json();
            console.log(movies)
            this.setState({
                movies: movies.Search,
                totalpages: movies.totalResults
            });
        } catch (e) {
            console.log(e);
        }
    } 

    async componentDidMount() {
        try {
            const res = await fetch(`http://www.omdbapi.com/?i=tt3896198&apikey=fa281222&s=Batman&page=${this.state.current}`);
            const movies = await res.json();
            console.log(movies)
            this.setState({
                movies: movies.Search,
                totalpages: movies.totalResults
            });
        } catch (e) {
            console.log(e);
        }
    }

    render(){
        const { movies } = this.state;
        console.log(movies)
        return (
            <div>
                <MovieGrid>
                    {movies.map((movie, id) => <Movie key={id} details={movie}/>)}
                </MovieGrid>
                <Pagination onChange={this.onChange} current={this.state.current} total={25} />
            </div>
        );
    }
}

export default MovieList;

const MovieGrid = styled.div`
    display: grid;
    padding: 1rem;
    grid-template-columns: repeat(5, 1fr);
    grid-row-gap: 1rem;
    text-align: center;
`;
