import React, { Component } from "react";
import { connect } from "react-redux";
import styled from "styled-components";
import Pagination from "rc-pagination";
import { Row, Col } from "react-bootstrap";
import "rc-pagination/assets/index.css";
import { searchMovies } from "./redux/action";
import Movie from "./Movie";
import OopsContainer from "./MovieDetail";

class MovieList extends Component {
  state = {
    movies: [],
    current: 1
  };

  onChange = page => {
    console.log(page);
    this.setState(
      {
        current: page
      },
      () => {
        const { searchTerm } = this.props;
        const { current } = this.state;
        this.props.searchMovies(searchTerm, current);
      }
    );
  };

  //   async componentDidMount() {
  //     try {
  //       const res = await fetch(
  //         `http://www.omdbapi.com/?i=tt3896198&apikey=fa281222&s=Batman&page=${
  //           this.state.current
  //         }`
  //       );
  //       const movies = await res.json();
  //       this.setState({
  //         movies: movies.Search,
  //         totalpages: parseInt(movies.totalResults)
  //       });
  //     } catch (e) {
  //       console.log(e);
  //     }
  //   }

  render() {
    const { current, totalpages } = this.state;
    const { movies, error } = this.props;
    console.log(this.props);
    return (
      <div>
        <MovieGrid>
          {movies && movies > 0
            ? movies.map((movie, id) => <Movie key={id} details={movie} />)
            : ""}
        </MovieGrid>
        <Row
          className="justify-content-md-center"
          style={{ justifyContent: "center" }}
        >
          {totalpages && totalpages > 10 ? (
            <Pagination
              onChange={this.onChange}
              current={current}
              total={totalpages}
            />
          ) : (
            <OopsContainer>
              <img src={require("./oops.jpg")} alt="error" width="300" />
              <h2>{error}</h2>
            </OopsContainer>
          )}
        </Row>
      </div>
    );
  }
}

function mapStateToProps(state) {
  console.log(state);
  return { movies: state.movies, error: state.error };
}

export default connect(
  mapStateToProps,
  { searchMovies }
)(MovieList);

const MovieGrid = styled(Row)`
  padding: 0 3rem;
  margin: 0 auto;
  justify-content: center;
`;
