import React, { PureComponent } from "react";
import styled from "styled-components";
import Pagination from "rc-pagination";
import { Row, Col } from "react-bootstrap";
import "rc-pagination/assets/index.css";
import Movie from "./Movie";

class MovieList extends PureComponent {
  state = {
    movies: [],
    current: 1
  };

  onChange = page => {
    console.log(page);
    this.setState(
      {
        current: page
      },
      () => this.initialloading()
    );
  };

  async initialloading() {
    try {
      const res = await fetch(
        `http://www.omdbapi.com/?i=tt3896198&apikey=fa281222&s=Batman&page=${
          this.state.current
        }`
      );
      const movies = await res.json();
      console.log(movies);
      this.setState({
        movies: movies.Search,
        totalpages: parseInt(movies.totalResults)
      });
    } catch (e) {
      console.log(e);
    }
  }

  async componentDidMount() {
    try {
      const res = await fetch(
        `http://www.omdbapi.com/?i=tt3896198&apikey=fa281222&s=Batman&page=${
          this.state.current
        }`
      );
      const movies = await res.json();
      console.log(movies);
      this.setState({
        movies: movies.Search,
        totalpages: parseInt(movies.totalResults)
      });
    } catch (e) {
      console.log(e);
    }
  }

  render() {
    const { movies, totalpages, current } = this.state;
    console.log(this.props);
    return (
      <div>
        <Row>
          <Col lg={2} md={2}>
            <MovieGrid>
              {movies.map((movie, id) => (
                <Movie key={id} details={movie} />
              ))}
            </MovieGrid>
          </Col>
        </Row>
        <Row>
          {totalpages && totalpages > 10 ? (
            <Paginationcont>
              <Pagination
                onChange={this.onChange}
                current={current}
                total={totalpages}
              />
            </Paginationcont>
          ) : (
            ""
          )}
        </Row>
      </div>
    );
  }
}

export default MovieList;

const MovieGrid = styled.div`
  //   display: grid;
  //   padding: 1rem;
  //   grid-template-columns: repeat(5, 1fr);
  //   grid-row-gap: 1rem;
  //   text-align: center;
`;

const Paginationcont = styled.div`
  //   display: flex;
  //   justify-content: center;
`;
